	 from (select itnews.*, row_number() over (order by seq desc) as rown from itnews where (title like '%${search}%') or (writer like '%${search}%')) where rown between #{start} and #{end} 


	 from (select itnews.*, row_number() over (order by seq desc) as rown from itnews where (title like '%${search}%')) where rown between #{start} and #{end}


	 from (select itnews.*, row_number() over (order by seq desc) as rown from itnews where (writer like '%${search}%')) where rown between #{start} and #{end}
